#include<iostream>​#include<string>​#include<ctime>​enum class Weapons { FISTS, GUN, SHOTGUN, REVOLVER, SNIPER, MACHINE_GUN, MAX };class Zombie;class Player{public:	Weapons weapon;	float precision;	int life;	bool isAlive()	{		return life > 0;	}	void attack(Zombie &z) 	{		z.life = z.life - static_cast<int> (Player::weapon) * Player::precision;	};	Player() : 		weapon(Weapons(rand() % static_cast<int>( Weapons::MAX))),		precision ((rand() % 20)/10),		life(rand()%101),	}};class Zombie{public:	int distanceToPlayer;	float speed;	float damage;	int life;	void attack(Player&p)	{		if (distanceToPlayer == 0) {			p.life = p.life - damage;		}		else 		{			--distanceToPlayer;		}	};};